import Task from "./Task";
import React, { useState } from "react";

function TaskList({tasks, category}) {
  const [task, setTask] = useState(tasks);


  console.log(task)

 function handleClick(text){
    setTask(task.filter((tsk) => 
      tsk.text !== text
      )
    )
  }

  const allTasks = task.map((tsk) => ({
  text: tsk.text, 
  category: tsk.category }))



  const itemsToDisplay = task.filter((item) => {
    if(category === 'All')return true;
    return item.category === category
   } 
   )



 return (
    <div className="tasks">
      <Task key={tasks.text} text={allTasks} handleClick={handleClick} />
    </div>
  );
}

export default TaskList;
