import React, { useState } from "react";

function Task(tasks) {
  const [task, setTask] = useState(tasks);
  console.log(task)

  function handleClick(text){
    const newTaskArray = task.filter((tsk) => tsk.text !== text);
    setTask(newTaskArray)
  }
    const taskList = task.map((tsk) => (
    <React.Fragment>
      <div className="label">{tsk.category}</div>
      <div className="text">{tsk.text}</div>
      <button className="delete" onClick={() => handleClick(tsk.text)}>X</button>
    </React.Fragment>
      )
    )
  return ( 
    <div className="task">
     {taskList}
    </div>
  );
}

export default Task;
