import React from "react";

function Task({category, task, handleClick}){

  console.log(task)

  const itemsToDisplay = task.filter((item) => {
    if(category === 'All')return true;
    return item.category === category
   } 
   )

   const allItems = itemsToDisplay.map((tsk)=> (
     <>
      <div className="label">{tsk.category}</div>
      <div className="text">{tsk.text}</div>
      <button className="delete" onClick={()=> handleClick(tsk.text)} >X</button>
    </>
   ));



  return (
    <div className="task">
      <div className="label">{allItems.category}</div>
      <div className="text">{allItems.text}</div>
      <button className="delete" onClick={()=> handleClick(allItems.text)} >X</button>
    </div>
  );
}

export default Task;
