import React from "react";
import Task from "./Task";

function TaskList({tasks}) {

function onDelete(text){
  tasks.filter((tsk) => tsk.text !== text)
}

 return (
    <div className="tasks">
      {tasks.map((tsk) => (
      <Task key={tsk.text} category={tsk.category} text={tsk.text}/>
       )) }
    </div>
  );
}

export default TaskList;
